library(arrow); arrow_info(); df <- data.frame(int = 1:10, dbl = as.numeric(1:10), str = letters[1:10]); tbl <- arrow_table(df); print(tbl); tmp_file <- tempfile(fileext = ".parquet"); write_parquet(df, tmp_file, compression = "snappy"); print(paste("File written with snappy compression:", tmp_file)); df2 <- read_parquet(tmp_file); print(paste("File read successfully:", all.equal(df, df2))); print("Testing other Arrow capabilities:"); print(paste("- S3 capability:", arrow_with_s3())); print(paste("- Parquet capability:", arrow_with_parquet())); print(paste("- Dataset capability:", arrow_with_dataset())); print(paste("- Json capability:", arrow_with_json()));
