import requests, io, pyarrow.parquet as pq, logging; logging.basicConfig(level=logging.INFO); url = "https://d37ci6vzurychx.cloudfront.net/trip-data/yellow_tripdata_2023-01.parquet"; response = requests.get(url, stream=True); buffer = io.BytesIO(); [buffer.write(chunk) for chunk in response.iter_content(chunk_size=8192)]; buffer.seek(0); table = pq.read_table(buffer); print(f"SUCCESS: {table.num_rows} rows, {len(table.column_names)} columns")
